package com.example.myapplicationimport android.content.Intentimport android.os.Bundleimport android.util.Logimport android.view.Viewimport androidx.appcompat.app.AppCompatActivityimport androidx.recyclerview.widget.LinearLayoutManagerimport androidx.recyclerview.widget.RecyclerViewimport com.example.myapplication.bottomNav.BottomNavActivityimport com.example.myapplication.test.weather.WeatherActivityimport com.example.myapplication.databinding.DataBindingActivityimport com.example.myapplication.tab.TabActivityimport io.flutter.embedding.android.FlutterActivityclass MainActivity : AppCompatActivity(), ListViewItemClickListener {    private val items = listOf(        "跳转flutter module", "kotlin mvvm", "dataBinding", "顶部导航栏","底部导航栏"    )    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        LifecycleObserver<MainActivity>().register(this)        val recyclerView = findViewById<RecyclerView>(R.id.recycleView)        val layoutManager = LinearLayoutManager(this)        recyclerView.layoutManager = layoutManager        recyclerView.adapter = ListViewAdpter(items, this)    }    override fun onItemClick(view: View, index: Int) {        Log.i("view----:$view", "position----:$index")        when (index) {            0 -> {                //跳转到flutter  默认路由//                startActivity(FlutterActivity.createDefaultIntent(this))                //跳转到flutter 自定路由                startActivity(                    FlutterActivity.withNewEngine().initialRoute("/root").build(this)                )            }            1 -> {                val intent = Intent()                intent.setClass(this, WeatherActivity().javaClass)                startActivity(intent)            }            2 -> {                val intent = Intent()                intent.setClass(this, DataBindingActivity().javaClass)                startActivity(intent)            }            3 -> {                val intent = Intent()                intent.setClass(this, TabActivity().javaClass)                startActivity(intent)            }            4 -> {                val intent = Intent()                intent.setClass(this, BottomNavActivity().javaClass)                startActivity(intent)            }        }    }}