package com.example.myapplicationimport android.os.Bundleimport android.util.Logimport android.view.Viewimport android.widget.Buttonimport androidx.appcompat.app.AppCompatActivityimport androidx.recyclerview.widget.LinearLayoutManagerimport androidx.recyclerview.widget.RecyclerViewimport io.flutter.embedding.android.FlutterActivityclass MainActivity : AppCompatActivity(), ListViewItemClickListener {    private val items = listOf(        "跳转flutter module"    )    var button: Button? = null    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        Log.d("ss", "ffffff")        setContentView(R.layout.activity_main)        val recyclerView = findViewById<RecyclerView>(R.id.recycleView)        val layoutManager = LinearLayoutManager(this)        recyclerView.layoutManager = layoutManager        recyclerView.adapter = ListViewAdpter(items, this)    }    override fun onItemClick(view: View, index: Int) {        Log.i("view----:$view", "position----:$index")        when (index) {            0 -> {                //跳转到flutter  默认路由//                startActivity(FlutterActivity.createDefaultIntent(this))                //跳转到flutter 自定路由                startActivity(                    FlutterActivity.withNewEngine().initialRoute("/root").build(this)                )            }        }    }}